mapper.select:
SELECT IMG
FROM PRODUCT
WHERE PRODUCT_NO = #{productNo}

mapper.update:
		UPDATE BOARD_IMG SET
			ORIGIN_NAME = #{originName},
			CHANGE_NAME = #{changeName}
    	WHERE IMG_NO = #{imgNo}

		UPDATE PRODUCT SET
			PRICE = #{price},
			QTY = #{qty},
			TITLE = #{title},
			CONTENT = #{content}

				, IMG = #{img}

    	WHERE PRODUCT_NO = #{productNo}
